  --secondary: hsl(195.0000 15.3846% 94.9020%);
  --secondary-foreground: hsl(210 25% 7.8431%);
  --muted: hsl(0 0% 9.4118%);
  --muted-foreground: hsl(210 3.3898% 46.2745%);
  --accent: hsl(205.7143 70% 7.8431%);
  --accent-foreground: hsl(203.7736 87.6033% 52.5490%);
  --destructive: hsl(356.3033 90.5579% 54.3137%);
  --destructive-foreground: hsl(0 0% 100%);
  --border: hsl(210 5.2632% 14.9020%);
  --input: hsl(207.6923 27.6596% 18.4314%);
  --ring: hsl(202.8169 89.1213% 53.1373%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/* Custom animations */
.pulse-green {
  animation: pulse-green 2s infinite;
}

@keyframes pulse-green {
  0%, 100% {
    background-color: var(--brand-green);
  }
  50% {
    background-color: hsl(160 78% 54%);
  }
}

.blink {
  animation: blink 1.5s infinite;
}

@keyframes blink {
  0%, 50% {
    opacity: 1;
  }
  51%, 100% {
    opacity: 0.3;
  }
}

.slide-up {
  animation: slideUp 0.5s ease-out;
}

@keyframes slideUp {
  from {
    transform: translateY(20px);
    opacity: 0;
  }
  to {
    transform: translateY(0);
    opacity: 1;
  }
}

/* Table hover effects */
.table-row-hover:hover {
  background-color: var(--gray-50);
}

/* Custom scrollbar */
.custom-scrollbar::-webkit-scrollbar {
  width: 6px;
  height: 6px;
}

.custom-scrollbar::-webkit-scrollbar-track {
  background: var(--gray-100);
  border-radius: 3px;
}

.custom-scrollbar::-webkit-scrollbar-thumb {
  background: var(--gray-300);
  border-radius: 3px;
}

.custom-scrollbar::-webkit-scrollbar-thumb:hover {
  background: var(--gray-600);
}

/* Table responsive */
.table-responsive {
  table-layout: fixed;
  width: 100%;
  min-width: 900px;
}

@media (max-width: 1200px) {
  .table-responsive {
    min-width: 800px;
  }
}

@media (max-width: 900px) {
  .table-responsive {
    min-width: 700px;
    font-size: 0.85rem;
  }

  .table-responsive th,
  .table-responsive td {
    padding: 0.25rem 0.125rem !important;
  }
}

/* Custom background colors for hierarchy levels */
.bg-blue-25 {
  background-color: var(--blue-25);
}

.bg-gray-25 {
  background-color: var(--gray-25);
}

.bg-purple-25 {
  background-color: var(--purple-25);
}

